sqlMap.select:
select sd.SECTION_DEFINITION_ID, sd.DEFAULT_LABEL, sd.LAYOUT_TYPE, sd.PAGE_TYPE, sd.ROLE, sd.SECTION_NAME, sd.SECTION_ORDER, sd.SITE_NAME from SECTION_DEFINITION sd where sd.PAGE_TYPE = #pageType# and (sd.SITE_NAME is NULL or sd.SITE_NAME = #siteName#) and (sd.ROLE is NULL or sd.ROLE in
#roles[]#
)
